import React, { Component } from 'react';
import DisplayTripInfo from './DisplayTripInfo';

class DisplayTrip extends Component {
  constructor() {
    super();
    this.state = {
      tripInfo: null,
      showButton: false,
    }
  }


  handleClick() {
    console.log('------ clickity click');

    this.setState({
      showButton: true,
    })
  }

  getTime = (timestamp) => {
      const time = new Date(timestamp)
      let hour = time.getUTCHours();
      let minutes = time.getUTCMinutes();
      console.log(time);
      console.log(hour);
      console.log(minutes);

      if (hour < 10 ){
        hour = ('0' + hour);
        console.log('hour:' + hour);
        return hour;
      }

      if (minutes < 10) {
        minutes = ('0' + minutes);
        console.log('minutes:' + minutes);
        return minutes;
      }

      return hour + ':' + minutes;
    }

  render(){
    const trips = this.props.trips["journeys"];

    console.log('----- this: ', this.getTime)
    return(
      <div className='allTrips container'  onClick={this.handleClick}>
        <ul className='tripList'>
          {trips.map((name, index) => {
            return <div className='trip'>
            <li key={ index} className='tripList'>
              <p>
               {this.getTime(name["legs"][0]["origin"]["departureTimePlanned"])}</p>
              <p>{name["legs"][0]["transportation"]["number"]} </p>

              {this.state.showButton && <button className='moreInfo'> Click for train info </button>}


              <DisplayTripInfo />

            </li>
            </div>;
          })}
        </ul>
      </div>
    )
  }
}

export default DisplayTrip;
